// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

contract PokemonFactory {

    //Event Here :
    event NewPokemon(uint pokemonId , string name ,uint attack , uint hp , string imgUrl);

    struct Pokemon {
        uint id ;
        string name ;
        uint attack ;
        uint hp ;
        string imgUrl;
    }

    Pokemon[] public pokemons ;

    mapping (uint => address) public pokemonToOwner;
    mapping (address => uint) ownerPokemonsCount;
    

    function _createPokemon( string memory _name, uint _attack, uint _hp ,  string memory _imgUrl) public {
        uint id = pokemons.length;
        pokemons.push(Pokemon( id, _name, _attack,_hp , _imgUrl));
        pokemonToOwner[id] = msg.sender;
        ownerPokemonsCount[msg.sender]++;
        emit NewPokemon(id, _name, _attack,_hp ,  _imgUrl);
    } 
    
    function getPokemonByOwner(address owner) external view returns(uint[] memory) {
    uint[] memory result = new uint[](ownerPokemonsCount[owner]);
    uint counter = 0;
    for (uint i = 0; i < pokemons.length; i++) {
      if (pokemonToOwner[i] == owner) {
        result[counter] = i;
        counter++;
      }
    }
    return result;
  }


    function attackEnemy(uint pokemonId, uint enemyId) public {
    require(msg.sender == pokemonToOwner[pokemonId]);
    Pokemon storage myPokemon = pokemons[pokemonId];
    Pokemon storage enemyPokemon  = pokemons[enemyId];
    if (myPokemon.attack > enemyPokemon.attack ){
        myPokemon.hp  = myPokemon.hp - ( myPokemon.attack - enemyPokemon.attack) ;
        myPokemon.attack = myPokemon.attack + 5 ;
        delete pokemons[enemyId];

    }
    if (myPokemon.attack == enemyPokemon.attack ){
        delete pokemons[enemyId];
        delete pokemons[pokemonId];

       
    }
    if (myPokemon.attack < enemyPokemon.attack || myPokemon.hp <=0 ){
        delete pokemons[pokemonId];
    }
    // start here
  }
}
